int Config::getPageIntervalMs() {
  if (!doc["display"]["page_ms"].is<int>()) {
    Serial.println("FATAL: Missing or invalid display.page_ms in config");
    for (;;);
  }
  return doc["display"]["page_ms"].as<int>();
}

